import { useState, useEffect } from "react";
import Char from "./Char";
import { getCharacters } from '../service';


const CharList = () => {
    const [charList, setCharList] = useState([]);
    const [backgroundChar, setBackgroundChar] = useState()
    const [isLoading, setIsLoading] = useState(false);
    /* LLAMADA A LA API*/
    useEffect(() => {
        setIsLoading(true);
        getCharacters()
          .then((data) => setCharList(data.results))
          .catch((err) => console.log(err))
          .finally(() => setIsLoading(false));
      }, []);

    return (
        <div className='page' style={{ backgroundChar }}>
            {isLoading && <span className='loading-text'>Image</span>}
            {charList.map((char) => (
                <Char
                    name={char.name}
                    status={char.status}
                    species={char.species}
                    setBackgroundChar={setBackgroundChar}
                />
            ))}
        </div>
    );
}
export default CharList;
