import Color from "./Color";

const colors = [
    {
        id:1,
        name:'Rojo',
        hex: '#BF0603'
    },
    {
        id: 2,
        name: 'Anaranjado',
        hex: '#C43721'
    },
    {
        id: 3,
        name: 'Amarillo',
        hex: '#ffd60a'
    }
];
//map sirve para iterar una lista
const ColorList =() => {
    return (
        colors.map( color => 
            <Color name={colors.name} hex={colors.hex} key={colors.id} id={colors.id}/>
        )
    );
}
export default ColorList;
